import React from 'react';
import { Form, Button } from 'react-bootstrap'
import axios from 'axios'
import Header from './Header'
import { propTypes } from 'react-bootstrap/esm/Image';

const UpdateEntry = ( props ) =>  {
  const [name, setName] = React.useState(props.location.state ? props.location.state.name : "");

  const [address, setAddress] = React.useState('');
  const [update, setUpdate] = React.useState('');

  const handleChangeName  = e => {
      setName(e.target.value);
      console.log(name);
  }

  const handleChangeAddress  = e => {
      setAddress(e.target.value);
  }

  const handleSubmit = (e) =>{
    e.preventDefault();
    console.log('hi');
    const person = {
      "address": address
    };
    setUpdate(person.address)
    axios.post(`http://localhost:9000/update/${name}`, person)
    .then((res) => {
      console.log(res.data);
    })}
    return (
    <div>
    <Header />
    <Form onSubmit={handleSubmit}>
    <Form.Group className="mb-3" controlId="formBasicAddress"><br />
    <Form.Label>Enter a name</Form.Label>
    <Form.Control type="text" placeholder="hi" onChange={handleChangeName} value={name} /><br />
    <Form.Label>Enter an address</Form.Label>
    <br />
    <Form.Control type="text" placeholder="Address" onChange={handleChangeAddress} value={address} />
    <br /><br />
    <Button variant="primary" type="submit">
      Submit
    </Button>
  </Form.Group>
        </Form>
        <label>{update}</label>
        <label>hi</label>
        <label></label>
        </div>
    )
}

export default UpdateEntry
