import React from 'react';
import { Form, Button } from 'react-bootstrap'
import axios from 'axios'
import Header from './Header'

const AddEntry = () =>  {

  const [name, setName] = React.useState('');
  const [address, setAddress] = React.useState('');
  const [update, setUpdate] = React.useState('');

  const handleChangeName  = e => {
      setName(e.target.value);
      console.log(name);
  }

  const handleChangeAddress  = e => {
      setAddress(e.target.value);
  }

  const handleSubmit = (e) =>{
    e.preventDefault();
    console.log('hi');
    const person = {
      "name": name,
      "address": address
    };
    //setName("");
    //setAddress("")
    axios.post(`http://localhost:9000/person/save`, person)
    .then((res) => {
      console.log(res.data);
      // save the new post to posts
      setUpdate('Entry inserted!')
    })}
    return (
        <div>
    <Header />
    <Form onSubmit={handleSubmit}>
    <Form.Group className="mb-3" controlId="formBasicAddress">
    <Form.Label>Email address</Form.Label>
    <Form.Control type="text" placeholder="Name" onChange={handleChangeName} value={name} />
    <Form.Text className="text-muted">
      Enter a name.
    </Form.Text>
    <br />
    <Form.Control type="text" placeholder="Address" onChange={handleChangeAddress} value={address} />
    <Form.Text className="text-muted">
      Enter an address.
    </Form.Text>
    <br /><br />
    <Button variant="primary" type="submit">
      Submit
    </Button>
  </Form.Group>
        </Form>
        <label>{update}</label>
        <label>hi</label>
        </div>
    )
}

export default AddEntry
