import React from 'react'
import { Form, Button, Alert, Table } from 'react-bootstrap'
import axios from 'axios'
import Header from './Header'

const Search = () => {
    const [name, setName] = React.useState("");
    const [update, setUpdate] = React.useState('');
    const [error, setError] = React.useState('');
    const [persons, setPersons] = React.useState([]);
    const handleChangeName  = e => {
        setName(e.target.value);
        console.log(name);
    }

    const searchTable = () => {
        if(persons.length == 0) {
            return "";
        } else {
            return (<Table>
        <thead><tr><th>Name</th><th>Address</th></tr></thead>
        {persons.length}
            </Table>)
    }}
  
    const handleSubmit = (e) =>{
        e.preventDefault();
        if(name == "") {
            setError(true)
        } else {
            setError(false)
        const person = {
           "name": name
        };
      setUpdate(person.address)
      axios.get(`http://localhost:9000/person/${name}`)
      .then((res) => {
        setPersons(res.data);
      })}}
    return (
            <div>
            <Header />
            <br />
            <Alert variant='danger' show={error}>Please enter a name to search for</Alert>
            <Form onSubmit={handleSubmit}><br />
            <h3><Form.Label>Search for an entry</Form.Label></h3>
            <Form.Group className="mb-3" controlId="formBasicAddress">
            <Form.Control type="text" placeholder="Name" onChange={handleChangeName} value={name} />
            <br />
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form.Group>
                </Form>
                {searchTable()}
                {error}
                {persons.length}

                </div>
            )
}

export default Search
