import React from 'react'
import { Form, Button, Alert } from 'react-bootstrap'
import axios from 'axios'
import Header from './Header'

const Search = () => {
    const [name, setName] = React.useState("");
    const [update, setUpdate] = React.useState('');
    const [error, setError] = React.useState('');
    const [persons, setPersons] = React.useState([]);
    const handleChangeName  = e => {
        setName(e.target.value);
        console.log(name);
    }
  
    const handleSubmit = (e) =>{
        e.preventDefault();
        if(name == "") {
            setError(true)
        } else {
            setError(false)
        const person = {
           "name": name
        };
      setUpdate(person.address)
      axios.get('http://localhost:9000/persons')
      .then((res) => {
        setPersons(res.data);
      })}}
    return (
            <div>
            <Header />
            <br /><br />
            <Alert variant='danger' show={error}>Please enter a name to search for</Alert>
            <Form onSubmit={handleSubmit}><br /><br />
            <h3><Form.Label>Search for an entry</Form.Label></h3> <br />
            <Form.Group className="mb-3" controlId="formBasicAddress">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="text" placeholder="Name" onChange={handleChangeName} value={name} />
            <Form.Text className="text-muted">
              Enter a name.
            </Form.Text>
            <br /><br />
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form.Group>
                </Form>
                <label>{persons.length}</label>
                <label>hi</label>
                </div>
            )
}

export default Search
