import React, { Component } from 'react'
import axios from 'axios'
import Header from './Header'
import { Table, Button } from 'react-bootstrap'

export default class PersonList extends React.Component {
    state = {
      persons: [],
      test: "hi"
    }

    deletes = (id, e) => {
      e.preventDefault();
      axios.delete(`http://localhost:9000/delete/${id}`)
      this.setState({persons: this.state.persons.filter(function(person) { 
        return person.name !== id
       })});
    }

    update = (id, e) => {
      e.preventDefault();
      this.context.router.push({ //browserHistory.push should also work here
        pathname: '/UpdateEntry',
        state: {"name": id}
      }); 
    }
  
    componentDidMount() {
      axios.get(`http://localhost:9000/persons`)
        .then(res => {
          const persons = res.data;
          this.setState({ persons });
        })
    }
  
    render() {
      return (
        <div><Header  striped borderless/>
        <Table>
          <thead>
            <tr>
            <th>Name</th>
            <th>Address</th>
            <th><Button variant="danger">Primary</Button></th>
            <th></th>
            </tr>
            </thead>
                { this.state.persons.map(person => <tr><td>{person.name}</td><td>{person.address}</td>
                <td style={{marginBottom: 20}}><Link to={{pathname: '/UpdateEntry', state: { "name": person.name}}}>Update</Link>
              </td>
                <td><Button onClick={(e) => this.deletes(person.name, e)} variant="outline-danger">Delete</Button>
                 </td></tr>)}
        </Table>
        {this.state.test}
        </div>
      )
    }
  }
